<?phpnamespace dvijok\core;class Session {		public $flashPrefix = 'flash';		public function __construct() {									}		public function set($key, $value) {				$_SESSION[$key] = $value;	}		public function get($key) {				if(isset($_SESSION[$key]))		{						return $_SESSION[$key];					}		return false;	}		public function has($key) {				if(isset($_SESSION[$key]))		{			return true;		}		return false;	}		public function remove($key) {				unset($_SESSION[$key]);		return true;	}		public function hasFlash($key, $remove = false) {				if(isset($_SESSION[$this->flashPrefix][$key]))		{			if($remove)			{				unset($_SESSION[$this->flashPrefix][$key]);			}			return true;		}		return false;			}		public function setFlash($key, $value) {				$_SESSION[$this->flashPrefix][$key] = $value;		return true;			}		public function getFlash($key) {				if(isset($_SESSION[$this->flashPrefix][$key]))		{			$value = $_SESSION[$this->flashPrefix][$key];			//unset($_SESSION[$this->flashPrefix][$key]);			return $value;		}		return false;	}		public function clearFlash() {				$_SESSION[$this->flashPrefix] = array();	}			}